
$(document).ready(()=>{let menu=$(".menu");$(".burger").click(()=>{menu.addClass("menu__mobile");});$(".close").click(()=>{menu.removeClass("menu__mobile");});$(".menu__item__link").click(()=>{menu.removeClass("menu__mobile");});$("#discount").click(()=>{$(".popap-discount").css("display","flex");});$(".popap-discount__cancel__close, .popap-discount").click((e)=>{if(e.target.className==="popap-discount"||e.target.className==="popap-discount__cancel__close"){$(".popap-discount").css("display","none");}});$(".masters__slider").slick({slidesToShow:3,slidesToScroll:1,arrows:true,dots:true,centerMode:true,variableWidth:true,speed:500,infinite:true,responsive:[{breakpoint:767,settings:{slidesToShow:1,centerMode:false,variableWidth:false,dots:false,},},],});ymaps.ready(function(){var myMap=new ymaps.Map("map",{center:[55.02362,82.925523],zoom:9,controls:[],});var myGeoObjects=[];myGeoObjects=new ymaps.Placemark([55.02362,82.925523],{balloonContentBody:"Текст в балуне",},{iconLayout:"default#image",iconImageHref:"../images/location.png",iconImageSize:[60,60],iconImageOffset:[-35,-35],});var clusterer=new ymaps.Clusterer({clusterDisableClickZoom:false,clusterOpenBalloonOnClick:false,});clusterer.add(myGeoObjects);myMap.geoObjects.add(clusterer);myMap.behaviors.disable("scrollZoom");});});